import React,{Component} from 'react';
import { Card, CardImg, CardBody, CardText, CardTitle,Breadcrumb,BreadcrumbItem,Button,Modal, ModalHeader, ModalBody, Label,  Row } from 'reactstrap';
import {Link} from 'react-router-dom';
import { Control, LocalForm, Errors } from 'react-redux-form';

const required = (val) => (val) && (val.length);
const maxLength = (len) => (val) => !(val) || (val.length <= len);
const minLength = (len) => (val) => !(val) || (val.length >= len);


class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            isModalOpen: false
        }
    }
    toggleModal = () => {
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });
    }
    handleSubmit(values) {
        this.toggleModal();
        console.log(values);
        alert('Comment : ' + JSON.stringify(values))
        //this.props.addComment(values.Rating, values.name, values.Comment);
    }
    render() {
        return (
            <React.Fragment>
                <Button  outline onClick={this.toggleModal}><span className="fa fa-pencil fa-lg"></span> Submit Comment</Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                    <ModalBody>
                        <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                            <Row className="form-group ml-1 mr-1">
                                <Label htmlFor="rating">Rating</Label>
                                <Control.select className="form-control" model=".rating" name="rating"
                                    className="form-control">
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </Control.select>    
                            </Row>
                            <Row className="form-group ml-1 mr-1">
                                <Label  htmlFor="name" >Your Name</Label>
                                <Control.text className="form-control ml-2" model=".name" id="name" name="name"
                                    placeholder="Your Name"
                                    className="form-control"
                                    validators={{
                                        required, minLength: minLength(3), maxLength: maxLength(15)
                                    }}
                                />
                                <Errors
                                    className="text-danger"
                                    model=".name"
                                    show="touched"
                                    messages={{
                                        required: 'Required',
                                        minLength: 'Must be greater than 2 characters',
                                        maxLength: 'Must be 15 characters or less'
                                    }}
                                 />
                            </Row>
                            <Row className="form-group ml-1 mr-1">
                                <Label className="ml-2" htmlFor="Comment">Comment</Label>
                                    <Control.textarea className="form-control " 
                                    model=".Comment" id="Comment" name="Comment"
                                        rows="6"
                                        className="form-control" />
                            </Row>
                            <Row className="form-group ml-2">
                                <Button  type="submit" color="primary"> Submit</Button>
                            </Row>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </React.Fragment>
        );
    }
}


    function RenderDish({dish}) {
        return (
            <div className="col-12 col-md-5 m-1">
                <Card>
                    <CardImg top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            </div>
        );       
    }

    function RenderComments({comments}) {
        if (comments != null) {
            return(
                <div className="col-12 col-md-5 m-1">
                    <h4>Comments</h4>
                    <ul className="list-unstyled">
                        {comments.map((comment)=>{
                            return(
                                <li key={comment.id}>
                                    <p>{comment.comment}</p>
                                    <p>-- {comment.author},
                                    &nbsp;
                                    {new Intl.DateTimeFormat('en-US', {
                                            year: 'numeric',
                                            month: 'short',
                                            day: '2-digit'
                                    }).format(new Date(Date.parse(comment.date)))}
                                    </p>
                                </li>
                            );
                        })}                   
                    </ul>
                    <CommentForm/>
               </div>
            );
        }
        else{
            return(<div></div>);
        }
    }

    const DishDetail = (props)=>{
        const dish = props.dish
        if (dish != null) {
            return (
                <div className="container">
                     <div className="row">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                        </Breadcrumb> 
                        <div className="col-12">
                            <h3>{props.dish.name}</h3>
                            <hr />
                        </div>  
                    </div>
                    <div  className="row">
                        <RenderDish dish={props.dish}/>
                        <RenderComments comments={props.comments}/>
                    </div>
                </div>
            )
        }
        else{
            return (<div></div>)
        }
    }
export default DishDetail
