import React,{Component} from 'react';
import { Card, CardImg, CardBody, CardText, CardTitle,Breadcrumb,BreadcrumbItem,Buttton } from 'reactstrap';
import {Link} from 'react-router-dom';

    class  CommentForm extends Component {
        constructor(props){
            super(props);
            this.state={
              isModalOpen: false
            };
            this.toggleModal=this.toggleModal.bind(this);
            // this.handleLogin = this.handleLogin.bind(this);
          }
          toggleModal(){
            this.setState({
            isModalOpen: !this.state.isModalOpen});
          } 
        render(){
            return(
                <button type="button"onClick={this.toggleModal}><span className="fa fa-pencil fa-lg"></span> Submit Comment</button>
            );
        }
    }

    function RenderDish({dish}) {
        return (
            <div className="col-12 col-md-5 m-1">
                <Card>
                    <CardImg top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            </div>
        );       
    }

    function RenderComments({comments}) {
        if (comments != null) {
            return(
                <div className="col-12 col-md-5 m-1">
                    <h4>Comments</h4>
                    <ul className="list-unstyled">
                        {comments.map((comment)=>{
                            return(
                                <li key={comment.id}>
                                    <p>{comment.comment}</p>
                                    <p>-- {comment.author},
                                    &nbsp;
                                    {new Intl.DateTimeFormat('en-US', {
                                            year: 'numeric',
                                            month: 'short',
                                            day: '2-digit'
                                    }).format(new Date(Date.parse(comment.date)))}
                                    </p>
                                </li>
                            );
                        })}                   
                    </ul>
                    <CommentForm/>
               </div>
            );
        }
        else{
            return(<div></div>);
        }
    }

    const DishDetail = (props)=>{
        const dish = props.dish
        if (dish != null) {
            return (
                <div className="container">
                     <div className="row">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                        </Breadcrumb> 
                        <div className="col-12">
                            <h3>{props.dish.name}</h3>
                            <hr />
                        </div>  
                    </div>
                    <div  className="row">
                        <RenderDish dish={props.dish}/>
                        <RenderComments comments={props.comments}/>
                    </div>
                </div>
            )
        }
        else{
            return (<div></div>)
        }
    }
export default DishDetail
